import React, { useState } from 'react';
import './navbar.css';
import logo from '../../assets/logo.png';
import contactImg from '../../assets/contact.png';
import menu from '../../assets/menu.png';
import { Link } from 'react-scroll';
const Navbar = () => {
    const [showMenu, setShowMenu] = useState(false);
    return (
        <nav className='navbar'> 
        <img src={logo} alt='logo' className='logo'/>
        <div className="desktopMenu">
            <Link activeClass='active' to='intro' spy = {true} smooth = {true} offset={-100}  duration = {500} className='desktopMenuListItem'>Home</Link>
            <Link activeClass='active' to='skills' spy = {true} smooth = {true} offset={-80}  duration = {500} className='desktopMenuListItem'>About</Link>
            <Link activeClass='active' to='works' spy = {true} smooth = {true} offset={-100}  duration = {500} className='desktopMenuListItem'>Portfolio</Link>
            <Link activeClass='active' to='clients' spy = {true} smooth = {true} offset={-100}  duration = {500} className='desktopMenuListItem'>Clients</Link>
        </div>
        <button className= 'desktopMenuBtn' onClick={() => {
            document.getElementById('contact').scrollIntoView({behavior:'smooth'});
        }}>

            <img src={contactImg} alt='' className='desktopMenuImg'/>Contact Me</button>

        <img src={menu} alt='Menu' className='mobMenu'/> 
        <div className="navMenu">
            <Link activeClass='active' to='intro' spy = {true} smooth = {true} offset={-100}  duration = {500} className='listItem' onClick={() => setShowMenu(showMeny)}>Home</Link>
            <Link activeClass='active' to='skills' spy = {true} smooth = {true} offset={-80}  duration = {500} className='listItem' onClick={() => setShowMenu(showMeny)}>About</Link>
            <Link activeClass='active' to='works' spy = {true} smooth = {true} offset={-100}  duration = {500} className='listItem' onClick={() => setShowMenu(showMeny)}>Portfolio</Link>
            <Link activeClass='active' to='clients' spy = {true} smooth = {true} offset={-100}  duration = {500} className='listItem' onClick={() => setShowMenu(showMeny)}>Clients</Link>
            <Link activeClass='active' to='contact' spy = {true} smooth = {true} offset={-100}  duration = {500} className='listItem' onClick={() => setShowMenu(showMeny)}>Contact</Link>
        </div>    
        </nav>
    )
}
export default Navbar
